import React from 'react';
import Todos from './components/Todos';
import './App.css';

function App() {
    const [todos, setTodos] = React.useState(
      [
        {
          id: 1,
          title: 'Take out trash',
          completed: false
        },
        {
          id: 2,
          title: 'Dinner with wife',
          completed: false
        },
        {
          id: 3,
          title: 'Meeting with boss',
          completed: false
        }
      ]
    )

    const markComplete = (id) => {
      setTodos(
        todos.map(todo => todo.id === id ? 
          {...todo, completed : !todo.completed} : todo)
      )
    }

    return (
      <div className="App">
        <Todos todos={todos} markComplete={ markComplete }/> {/* todos is like an attribute of an html tag, but in this case is a prop */}
      </div>
    );
}

export default App;
