import React from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Header from './components/layout/Header';
import Todos from './components/Todos';
import AddTodo from './components/AddTodo';
import uuid from 'uuid';

import './App.css';

function App() {
    const [todos, setTodos] = React.useState(
      [
        {
          id: uuid.v4(),
          title: 'Take out trash',
          completed: false
        },
        {
          id: uuid.v4(),
          title: 'Dinner with wife',
          completed: false
        },
        {
          id: uuid.v4(),
          title: 'Meeting with boss',
          completed: false
        }
      ]
    )

    //https://stackoverflow.com/questions/55987953/how-do-i-update-states-onchange-in-an-array-of-object-in-react-hooks
    const markComplete = (id) => {
      setTodos(
        todos.map(todo => todo.id === id ? 
          {...todo, completed : !todo.completed} : todo)
      )
    }

    //Delete Todo
    const deleteTodo = (id) => {
      setTodos(
        todos.filter(todo => todo.id !== id)
      )
    }

    const addTodo = (title) => {
      const newTodo = {
        id: uuid.v4(),
        title: title,
        completed: false
      }
      setTodos(todos => [...todos, newTodo])
    }

    return (
      <Router>
        <div className="App">
          <div className="container">
            <Header />
            <Route path="/" render={ props => (
              <React.Fragment>
                <AddTodo addTodo={ addTodo } />
            <Todos todos={todos} markComplete={ markComplete } deleteTodo={ deleteTodo }/> {/* todos is like an attribute of an html tag, but in this case is a prop */}
              </React.Fragment>
            )} />
          </div>   
        </div>
      </Router>
    );
}

export default App;
